const initialState = {
  allIds: [0],
  byIds: {0 : {text:'lialia' , amount: 12}}
};

export default function reducer(state = initialState, action) {
  switch (action.type) {
    case "ADD_NEW": {
      const { id, text, amount } = action.playload;
      console.log(id, text, amount);
      return {
        ...state,
        allIds: [...state.allIds, id],
        byIds: {
          ...state.byIds,
          [id] : {
            text,
            amount
          }
        }
      };
    }
    default:
      return state;
  }
}
