import React from "react";
import { connect } from "react-redux";

const HisList = ({ his }) => (
  <li
    className={`history_record ${his.amount > 0 ? 'history_record_income' : 'history_record_expense'}`}>
    <span> {his.text} </span>
    <span> {his.amount} </span>
  </li>
);

const Todo = ({ todo, toggleTodo }) => (
  <li className="todo-item" onClick={() => toggleTodo(todo.id)}>
    {todo && todo.completed ? "ðŸ‘Œ" : "ðŸ‘‹"}{" "}
    <span
      className={cx(
        "todo-item__text",
        todo && todo.completed && "todo-item__text--completed"
      )}
    >
      {todo.content}
    </span>
  </li>
);

export default connect()(HisList);
